package com.syntax.class09;

public class PrintingPatternBestWay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		for (int i=5; i>=1; i--) {
			for (int c=7; c>=1; c--) {
				System.out.print(c);
			}
		System.out.println();
		}
		
		for (int r = 1; r <= 4; r++) {
			for (int t = 1; t <= r; t++) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		
		
		
		
		
		
	}

}
