package com.syntax.class09;

public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int l=0; l<6; l++) { // outer loop!
			
			System.out.println(l); 
			
			for  (int r=0; r<+99999; r++) { // inner loop!
				
				System.out.println(r);
				}
			System.out.println("------------------------------");
		}
	
	}

}
